	public function __construct()
	{
		$this->firstNameCount = count( $this->firstNames );
		$this->lastNameCount = count( $this->lastNames );
		$this->emailAddressCount = count( $this->emailAddresses );
	}

	public function getFirstName()
	{
		return $this->firstNames[ rand( 0, $this->firstNameCount-1 ) ];
	}

	public function getLastName()
	{
		return $this->lastNames[ rand( 0, $this->lastNameCount-1 ) ];
	}

	public function getEmailAddress()
	{
		return $this->emailAddresses[ rand( 0, $this->emailAddressCount-1 ) ];
	}

